        const id=req.params.id;
        const {results}=await db.query(
            'SELECT * FROM listings WHERE id=?',
            [id],
        );
        const listing=results[0];
        if(!listing) throw Boom.notFound(`Listing does not exist with id ${id}`);
        return listing;

    }

}

